import React, { useRef, useEffect } from "react";
import { gsap } from "gsap";
import { Link } from "react-router-dom";

import {
  staggerText,
  staggerReveal,
  fadeInUp,
  handleHover,
  handleHoverExit,
  // handleCityReturn,
  // handleCity,
  staggerRevealClose
} from "./Animation";

// import aboutimg from "../../images/ss.jpg";
// import skillsimg from "../../images/skills.jpg";
// import workimg from "../../images/work.jpg";
// import contactimg from "../../images/contact.jpg";

// const about = [
//   { name: "About", image: aboutimg },
// ];

// const skills = [
//   { name: "Skills", image: skillsimg },
// ];

// const work = [
//   { name: "My Work", image: workimg },
// ];

// const contact = [
//   { name: "Contact", image: contactimg },
// ];


const Hamburger = ({ state }) => {
  // Create varibles of our dom nodes
  let menuLayer = useRef(null);
  let reveal1 = useRef(null);
  let reveal2 = useRef(null);
  //let cityBackground = useRef(null);
  let line1 = useRef(null);
  let line2 = useRef(null);
  let line3 = useRef(null);
  let line4 = useRef(null);
  let info = useRef(null);

  useEffect(() => {
    // If the menu is open and we click the menu button to close it.
    if (state.clicked === false) {
      // If menu is closed and we want to open it.

      staggerRevealClose(reveal2, reveal1);
      // Set menu to display none
      gsap.to(menuLayer, { duration: 1, css: { display: "none" } });
    } else if (
      state.clicked === true ||
      (state.clicked === true && state.initial === null)
    ) {
      // Set menu to display block
      gsap.to(menuLayer, { duration: 0, css: { display: "block" } });
      //Allow menu to have height of 100%
      gsap.to([reveal1, reveal2], {
        duration: 0,
        opacity: 1,
        height: "100%"
      });
      staggerReveal(reveal1, reveal2);
      fadeInUp(info);
      staggerText(line1, line2, line3, line4);
    }
  }, [state]);

  return (
    <div ref={el => (menuLayer = el)} className='hamburger-menu'>
      <div
        ref={el => (reveal1 = el)}
        className='menu-secondary-background-color'></div>
      <div ref={el => (reveal2 = el)} className='menu-layer'>
        <div
          // ref={el => (cityBackground = el)}
          className='menu-city-background'></div>
        <div className='container'>
          <div className='wrapper'>
            <div className='menu-links'>
              <nav>
                <ul>
                  <li>
                    <Link
                      onMouseEnter={e => handleHover(e)}
                      onMouseOut={e => handleHoverExit(e)}
                      ref={el => (line1 = el)}
                      to={`/model/shruti-panjwani`}>
                        About
                      {/* {about.map(el => (
                        <span
                          key={el.name}
                          onMouseEnter={() => handleCity(el.image, cityBackground)}
                          onMouseOut={() => handleCityReturn(cityBackground)}>
                          {el.name}
                        </span>
                      ))} */}
                    </Link>
                  </li>
                  <li>
                    <Link
                      onMouseEnter={e => handleHover(e)}
                      onMouseOut={e => handleHoverExit(e)}
                      ref={el => (line2 = el)}
                      to='/skills'>
                        Skills
                      {/* {skills.map(el => (
                        <span
                          key={el.name}
                          onMouseEnter={() => handleCity(el.image, cityBackground)}
                          onMouseOut={() => handleCityReturn(cityBackground)}>
                          {el.name}
                        </span>
                      ))} */}
                    </Link>
                  </li>
                  <li>
                    <Link
                     onMouseEnter={e => handleHover(e)}
                     onMouseOut={e => handleHoverExit(e)}
                     ref={el => (line3 = el)}
                     to='/work'>
                       Work
                     {/* {work.map(el => (
                       <span
                         key={el.name}
                         onMouseEnter={() => handleCity(el.image, cityBackground)}
                         onMouseOut={() => handleCityReturn(cityBackground)}>
                         {el.name}
                       </span>
                     ))} */}
                    </Link>
                  </li>
                  <li>
                    <Link
                      onMouseEnter={e => handleHover(e)}
                      onMouseOut={e => handleHoverExit(e)}
                      ref={el => (line4 = el)}
                      to='/contact'>
                        Contact
                      {/* {contact.map(el => (
                        <span
                          key={el.name}
                          onMouseEnter={() => handleCity(el.image, cityBackground)}
                          onMouseOut={() => handleCityReturn(cityBackground)}>
                          {el.name}
                        </span>
                      ))} */}
                    </Link>
                  </li>
                </ul>
              </nav>
              <div ref={el => (info = el)} className='info'>
                <h3>Our Promise</h3>
                <br />
                <p>
                  The passage experienced a surge in popularity during the 1960s
                  when Letraset used it on their dry-transfer sheets, and again
                  during the 90s as desktop publishers bundled the text with
                  their software.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Hamburger;
